mapper.select:
SELECT
a.*
FROM
open_menu a


                and a.parent_id = #{parentId}


                and a.tree_path = #{treePath}


                and a.name = #{name}


                and a.menu_type = #{menuType}


                and a.path = #{path}


                and a.component = #{component}


                and a.perm = #{perm}


                and a.visible = #{visible}


                and a.sort_value = #{sortValue}


                and a.icon = #{icon}


                and a.redirect = #{redirect}


                and a.del_flag = #{delFlag}


                and a.tenant_id = #{tenantId}

SELECT
a.*
FROM
open_menu a


                and a.parent_id = #{search.parentId}


                and a.tree_path = #{search.treePath}


                and a.name = #{search.name}


                and a.menu_type = #{search.menuType}


                and a.path = #{search.path}


                and a.component = #{search.component}


                and a.perm = #{search.perm}


                and a.visible = #{search.visible}


                and a.sort_value = #{search.sortValue}


                and a.icon = #{search.icon}


                and a.redirect = #{search.redirect}


                and a.del_flag = #{search.delFlag}


                and a.tenant_id = #{search.tenantId}
