mapper.select:
    SELECT a.* FROM sys_user a


	        and a.dept_id = #{deptId}


            and a.user_name = #{userName}


            and a.nick_name = #{nickName}


            and a.user_type = #{userType}


            and a.email = #{email}


            and a.phonenumber = #{phonenumber}


            and a.sex = #{sex}


            and a.status = #{status}


            and a.del_flag = #{delFlag}


            and a.login_ip = #{loginIp}
